import React, { useEffect, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css'; // Leaflet CSS
import './App.css'; // Custom CSS for the map container

function App() {
  const [map, setMap] = useState(null);
  const [boundariesLayer, setBoundariesLayer] = useState(null);
  const [popup, setPopup] = useState(null); // State to manage the popup

  // Initialize the map when the component mounts
  useEffect(() => {
    // Coordinates for Dublin, Ireland
    const dublinCoords = [53.3498, -6.2603]; // Latitude, Longitude

    // Create the map
    const mapInstance = L.map('map').setView(dublinCoords, 12); // Centered on Dublin with zoom level 12

    // Add the tile layer (OpenStreetMap)
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors',
    }).addTo(mapInstance);

    setMap(mapInstance);

    // Load the boundaries GeoJSON data
    fetch('/layers/Dublin_SA1_TOTALPOP.geojson') // Update the path to your GeoJSON file
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to load GeoJSON data');
        }
        return response.json();
      })
      .then(data => {
        console.log('GeoJSON data loaded successfully:', data); // Debugging

        const boundariesLayer = L.geoJSON(data, {
          style: {
            color: 'black', // Set the outline color to black
            weight: 0.5,     // Set the outline thickness to very thin
            fillOpacity: 0   // Make the fill transparent
          },
          onEachFeature: (feature, layer) => {
            // Add a click event to each feature
            layer.on('click', (e) => {
              // Get the total_pop value from the feature properties
              const totalPop = feature.properties.total_pop;

              // Create a popup at the clicked location
              if (popup) {
                popup.remove(); // Remove the existing popup if it exists
              }
              const newPopup = L.popup()
                .setLatLng(e.latlng) // Set the popup location to the clicked point
                .setContent(`Total Population: ${totalPop}`) // Display the total_pop value
                .openOn(mapInstance);
              setPopup(newPopup); // Update the popup state
            });
          }
        });
        setBoundariesLayer(boundariesLayer);
        boundariesLayer.addTo(mapInstance); // Add the layer to the map by default
      })
      .catch(error => {
        console.error('Error loading boundaries:', error); // Debugging
      });

    // Cleanup function to remove the map when the component unmounts
    return () => {
      mapInstance.remove();
    };
  }, [popup]); // Add popup as a dependency to ensure proper cleanup

  return (
    <div className="App">
      <h1>Dublin Population Density Rail Map</h1>
      <div className="map-container">
        <div id="map"></div> {/* Leaflet map container */}
      </div>
    </div>
  );
}

export default App;
