// src/App.js
import React, { useEffect, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css'; // Leaflet CSS
import './App.css'; // Custom CSS for the map container

function App() {
  const [map, setMap] = useState(null);
  const [boundariesLayer, setBoundariesLayer] = useState(null);

  // Initialize the map when the component mounts
  useEffect(() => {
    const dublinCoords = [53.3498, -6.2603]; // Latitude, Longitude

    const mapInstance = L.map('map').setView(dublinCoords, 12);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors',
    }).addTo(mapInstance);

    setMap(mapInstance);

    fetch('/layers/Dublin_SA1_TOTALPOP.geojson') // Update path if necessary
      .then(response => response.json())
      .then(data => {
        const boundariesLayer = L.geoJSON(data, {
          style: {
            color: 'black',
            weight: 0.5,
            fillOpacity: 0
          },
          onEachFeature: (feature, layer) => {
            layer.on('click', function () {
              // Reset all layers to default
              boundariesLayer.eachLayer(l => {
                l.setStyle({
                  color: 'grey',
                  weight: 0.5,
                  fillOpacity: 0.2 // Greyed out effect
                });
              });

              // Highlight the clicked feature
              layer.setStyle({
                color: 'red', // Highlighted color
                weight: 2,
                fillOpacity: 0.5
              });

              layer.bindPopup(`Total Population: ${feature.properties.total_pop}`).openPopup();
            });
          }
        });

        setBoundariesLayer(boundariesLayer);
        boundariesLayer.addTo(mapInstance);
      })
      .catch(error => console.error('Error loading boundaries:', error));

    return () => {
      mapInstance.remove();
    };
  }, []);



  return (
    <div className="App">
      <h1>Dublin Population Density Rail Map</h1>
      <div className="map-container">
        <div id="map"></div> {/* Leaflet map container */}
      </div>
    </div>
  );
}

export default App;
